    @IBOutlet weak var statePicker: UIPickerView!
    @IBOutlet weak var stateBtn: UIButton!
    @IBOutlet weak var stateLbl: UILabel!
    @IBOutlet weak var countryPicker: UIPickerView!
    @IBOutlet weak var countryBtn: UIButton!
    @IBOutlet weak var buyNowBtn: UIButton!
    
    
    
    override func viewDidLoad() {
        super.viewDidLoad()
        statePicker.dataSource = self
        statePicker.delegate = self
        countryPicker.dataSource = self
        countryPicker.delegate = self
    }

    override func didReceiveMemoryWarning() {
        super.didReceiveMemoryWarning()
        // Dispose of any resources that can be recreated.
    }

    @IBAction func stateBtnPressed(_ sender: AnyObject) {
        statePicker.isHidden = false
        buyNowBtn.isHidden = true
    }
    
    @IBAction func countryBtnPressed(_ sender: AnyObject) {
        countryPicker.isHidden = false
        buyNowBtn.isHidden = true
        stateLbl.isHidden = true
        stateBtn.isHidden = true
    }
    
    @IBAction func BuyNowBtnPressed(_ sender: AnyObject) {
        
    }
    
    func numberOfComponents(in pickerView: UIPickerView) -> Int {
        return 1
    }
    
    func pickerView(_ pickerView: UIPickerView, numberOfRowsInComponent component: Int) -> Int {
        if pickerView == countryPicker {
            return countries.count
        } else if pickerView == statePicker {
            return states.count
        }
        return 0
    }
    
    func pickerView(_ pickerView: UIPickerView, titleForRow row: Int, forComponent component: Int) -> String? {
        if pickerView == countryPicker {
            return countries[row]
        } else if pickerView == statePicker{
            return states[row]
        }
        return ""
    }
    
    func pickerView(_ pickerView: UIPickerView, didSelectRow row: Int, inComponent component: Int) {
        
        if pickerView == countryPicker {
            countryBtn.setTitle(countries[row], for: UIControlState.normal)
            countryPicker.isHidden = true
            if countries[row] == "United States" {
                stateLbl.isHidden = false
                stateBtn.isHidden = false
            } else {
                stateLbl.isHidden = true
                stateBtn.isHidden = true
                buyNowBtn.isHidden = false
            }
            
        } else if pickerView == statePicker {
            stateBtn.setTitle(states[row], for: UIControlState.normal)
            statePicker.isHidden = true
            buyNowBtn.isHidden = false
        }
        
       
    }

}

